mapper.sql:
WHERE 1 = 1


        AND account = #{entity.account,jdbcType=CHAR}


        AND account like CONCAT('%',#{entity.accountLike,jdbcType=CHAR},'%')


        AND nickname like CONCAT('%',#{entity.nicknameLike,jdbcType=CHAR},'%')


        AND password = #{entity.password,jdbcType=CHAR}


        AND deleted = #{entity.deleted,jdbcType=INTEGER}


        AND id = #{entity.id,jdbcType=BIGINT}


        AND type = #{entity.type,jdbcType=BIGINT}

